import React, { useState, useEffect } from 'react';
import { FiEye, FiEyeOff, FiCopy, FiPlus, FiSearch, FiMoon, FiSun, FiLogOut, FiEdit2, FiDownload } from 'react-icons/fi';
import { toast } from 'react-hot-toast';
import { passwordService } from '../services/api';

const Dashboard = ({ user, onLogout, onAddClick }) => {
  const [darkMode, setDarkMode] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [visiblePasswords, setVisiblePasswords] = useState({});
  const [passwords, setPasswords] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [editingPassword, setEditingPassword] = useState(null);

  useEffect(() => {
    const fetchPasswords = async () => {
      try {
        setIsLoading(true);
        const response = await passwordService.getPasswords(user.email);
        
        if (response.code === '130') {
          // Success - format the passwords for display
          const formattedPasswords = response.data.map(item => ({
            id: item.id,
            platform: item.name,
            email: user.email,
            password: item.password,
            notes: '',
            tags: ['saved']
          }));
          
          setPasswords(formattedPasswords);
        } else {
          toast.error('Failed to load passwords');
        }
      } catch (error) {
        console.error('Error fetching passwords:', error);
        toast.error('Failed to load passwords');
      } finally {
        setIsLoading(false);
      }
    };

    if (user && user.email) {
      fetchPasswords();
    }
  }, [user]);

  const togglePasswordVisibility = (id) => {
    setVisiblePasswords((prev) => ({
      ...prev,
      [id]: !prev[id],
    }));
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    toast.success('Copied to clipboard!');
  };

  const handleEditPassword = (password) => {
    setEditingPassword(password);
  };

  const handleUpdatePassword = async (updatedPassword) => {
    try {
      const response = await passwordService.updatePassword(updatedPassword);
      
      if (response.code === '150') {
        // Update local state
        setPasswords(passwords.map(p => 
          p.id === updatedPassword.id ? updatedPassword : p
        ));
        setEditingPassword(null);
        toast.success('Password updated successfully!');
      } else {
        toast.error(response.message || 'Failed to update password');
      }
    } catch (error) {
      console.error('Error updating password:', error);
      toast.error('Failed to update password');
    }
  };

  const handleExportPasswords = async (format) => {
    try {
      await passwordService.exportPasswords(user.email, format);
      toast.success(`Passwords exported as ${format.toUpperCase()} successfully!`);
    } catch (error) {
      console.error(`Error exporting passwords as ${format}:`, error);
      toast.error(`Failed to export passwords as ${format}`);
    }
  };

  const filteredPasswords = passwords.filter(
    (pass) =>
      pass.platform.toLowerCase().includes(searchQuery.toLowerCase()) ||
      pass.email.toLowerCase().includes(searchQuery.toLowerCase()) ||
      (pass.tags && pass.tags.some((tag) => tag.toLowerCase().includes(searchQuery.toLowerCase())))
  );

  return (
    <div className={`min-h-screen ${darkMode ? 'dark bg-gray-900' : 'bg-gray-50'}`}>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="flex justify-between items-center mb-8">
          <div>
            <h1 className={`text-3xl font-bold ${darkMode ? 'text-white' : 'text-gray-900'}`}>
              Welcome back, {user?.email?.split('@')[0] || 'User'}!
            </h1>
            <p className={`mt-2 ${darkMode ? 'text-gray-300' : 'text-gray-600'}`}>
              Manage your passwords securely
            </p>
          </div>
          <div className="flex items-center space-x-4">
            <div className="flex space-x-2">
              <button
                onClick={() => handleExportPasswords('xml')}
                className={`p-2 rounded-lg ${
                  darkMode ? 'bg-gray-800 text-yellow-400' : 'bg-white text-gray-600'
                } shadow-md hover:shadow-lg transition-all`}
                title="Export as XML"
              >
                <FiDownload size={20} />
              </button>
              <button
                onClick={() => handleExportPasswords('excel')}
                className={`p-2 rounded-lg ${
                  darkMode ? 'bg-gray-800 text-green-400' : 'bg-white text-gray-600'
                } shadow-md hover:shadow-lg transition-all`}
                title="Export as Excel"
              >
                <FiDownload size={20} />
              </button>
            </div>
            <button
              onClick={() => setDarkMode(!darkMode)}
              className={`p-2 rounded-lg ${
                darkMode ? 'bg-gray-800 text-yellow-400' : 'bg-white text-gray-600'
              } shadow-md hover:shadow-lg transition-all`}
            >
              {darkMode ? <FiSun size={20} /> : <FiMoon size={20} />}
            </button>
            <button
              onClick={onAddClick}
              className="flex items-center px-4 py-2 bg-[#2ecc71] text-white rounded-lg hover:bg-[#27ae60] transition-colors"
            >
              <FiPlus className="mr-2" />
              Add Password
            </button>
            <button
              onClick={onLogout}
              className="flex items-center px-4 py-2 bg-gray-200 text-gray-700 rounded-lg hover:bg-gray-300 transition-colors"
            >
              <FiLogOut className="mr-2" />
              Logout
            </button>
          </div>
        </div>

        <div className="mb-6">
          <div className="relative">
            <FiSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
            <input
              type="text"
              placeholder="Search passwords..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className={`w-full pl-10 pr-4 py-2 rounded-lg border ${
                darkMode
                  ? 'bg-gray-800 border-gray-700 text-white'
                  : 'bg-white border-gray-300 text-gray-900'
              } focus:outline-none focus:ring-2 focus:ring-[#2ecc71]`}
            />
          </div>
        </div>

        {isLoading ? (
          <div className="flex justify-center items-center h-64">
            <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-[#2ecc71]"></div>
          </div>
        ) : filteredPasswords.length === 0 ? (
          <div className={`text-center py-12 ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
            <p className="text-xl">No passwords found</p>
            <p className="mt-2">Add your first password to get started</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {filteredPasswords.map((pass) => (
              <div
                key={pass.id}
                className={`p-6 rounded-xl shadow-md ${
                  darkMode ? 'bg-gray-800 text-white' : 'bg-white text-gray-900'
                }`}
              >
                <div className="flex justify-between items-start mb-4">
                  <div>
                    <h3 className="text-lg font-semibold">{pass.platform}</h3>
                    <p className={`text-sm ${darkMode ? 'text-gray-400' : 'text-gray-500'}`}>
                      {pass.email}
                    </p>
                  </div>
                  <div className="flex space-x-2">
                    <button
                      onClick={() => handleEditPassword(pass)}
                      className={`p-2 rounded-lg ${
                        darkMode ? 'bg-gray-700 text-white' : 'bg-gray-100 text-gray-700'
                      } hover:bg-opacity-80 transition-colors`}
                      title="Edit password"
                    >
                      <FiEdit2 size={16} />
                    </button>
                    <button
                      onClick={() => copyToClipboard(pass.password)}
                      className={`p-2 rounded-lg ${
                        darkMode ? 'bg-gray-700 text-white' : 'bg-gray-100 text-gray-700'
                      } hover:bg-opacity-80 transition-colors`}
                      title="Copy password"
                    >
                      <FiCopy size={16} />
                    </button>
                    <button
                      onClick={() => togglePasswordVisibility(pass.id)}
                      className={`p-2 rounded-lg ${
                        darkMode ? 'bg-gray-700 text-white' : 'bg-gray-100 text-gray-700'
                      } hover:bg-opacity-80 transition-colors`}
                      title={visiblePasswords[pass.id] ? 'Hide password' : 'Show password'}
                    >
                      {visiblePasswords[pass.id] ? (
                        <FiEyeOff size={16} />
                      ) : (
                        <FiEye size={16} />
                      )}
                    </button>
                  </div>
                </div>
                <div className="relative">
                  <input
                    type={visiblePasswords[pass.id] ? 'text' : 'password'}
                    value={pass.password}
                    readOnly
                    className={`w-full px-3 py-2 rounded-lg border ${
                      darkMode
                        ? 'bg-gray-700 border-gray-600 text-white'
                        : 'bg-gray-50 border-gray-300 text-gray-900'
                    }`}
                  />
                </div>
                {pass.tags && pass.tags.length > 0 && (
                  <div className="mt-4 flex flex-wrap gap-2">
                    {pass.tags.map((tag, index) => (
                      <span
                        key={index}
                        className={`px-2 py-1 text-xs rounded-full ${
                          darkMode
                            ? 'bg-gray-700 text-gray-300'
                            : 'bg-gray-100 text-gray-600'
                        }`}
                      >
                        {tag}
                      </span>
                    ))}
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Edit Password Modal */}
      {editingPassword && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className={`p-6 rounded-xl shadow-lg w-full max-w-md ${
            darkMode ? 'bg-gray-800 text-white' : 'bg-white text-gray-900'
          }`}>
            <h2 className="text-2xl font-bold mb-4">Edit Password</h2>
            <form onSubmit={(e) => {
              e.preventDefault();
              handleUpdatePassword(editingPassword);
            }}>
              <div className="mb-4">
                <label className="block text-sm font-medium mb-1">Platform</label>
                <input
                  type="text"
                  value={editingPassword.platform}
                  onChange={(e) => setEditingPassword({
                    ...editingPassword,
                    platform: e.target.value
                  })}
                  className={`w-full px-3 py-2 rounded-lg border ${
                    darkMode
                      ? 'bg-gray-700 border-gray-600 text-white'
                      : 'bg-white border-gray-300 text-gray-900'
                  }`}
                />
              </div>
              <div className="mb-4">
                <label className="block text-sm font-medium mb-1">Email/Username</label>
                <input
                  type="text"
                  value={editingPassword.email}
                  onChange={(e) => setEditingPassword({
                    ...editingPassword,
                    email: e.target.value
                  })}
                  className={`w-full px-3 py-2 rounded-lg border ${
                    darkMode
                      ? 'bg-gray-700 border-gray-600 text-white'
                      : 'bg-white border-gray-300 text-gray-900'
                  }`}
                />
              </div>
              <div className="mb-4">
                <label className="block text-sm font-medium mb-1">Password</label>
                <input
                  type="text"
                  value={editingPassword.password}
                  onChange={(e) => setEditingPassword({
                    ...editingPassword,
                    password: e.target.value
                  })}
                  className={`w-full px-3 py-2 rounded-lg border ${
                    darkMode
                      ? 'bg-gray-700 border-gray-600 text-white'
                      : 'bg-white border-gray-300 text-gray-900'
                  }`}
                />
              </div>
              <div className="flex justify-end space-x-3">
                <button
                  type="button"
                  onClick={() => setEditingPassword(null)}
                  className={`px-4 py-2 rounded-lg ${
                    darkMode
                      ? 'bg-gray-700 text-white'
                      : 'bg-gray-200 text-gray-700'
                  }`}
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="px-4 py-2 bg-[#2ecc71] text-white rounded-lg hover:bg-[#27ae60] transition-colors"
                >
                  Save Changes
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
