import axios from 'axios';

// Base URL for the API
const API_URL = process.env.REACT_APP_API_URL || 'http://localhost/php_project/Lock Box/LockBox.php';

// API key for authentication
const API_KEY = process.env.REACT_APP_API_KEY || 'lock123';

// Create axios instance with default config
const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/x-www-form-urlencoded',
  },
  withCredentials: true, // Enable sending cookies in cross-origin requests
});

// Helper function to format data for PHP backend
const formatData = (data) => {
  return `json=${JSON.stringify({ ...data, api_key: API_KEY })}`;
};

// Authentication services
export const authService = {
  // Login user
  login: async (email, password) => {
    try {
      const response = await api.post('', formatData({ 
        email, 
        password,
        validate: true 
      }));
      
      if (response.data.code === 1) {
        // Store user data in localStorage
        const userData = {
          email: response.data.data.email,
          name: response.data.data.name,
          id: response.data.data.id,
          sessionId: response.data.data.session_id
        };
        localStorage.setItem('user', JSON.stringify(userData));
      }
      
      return response.data;
    } catch (error) {
      console.error('Login error:', error);
      throw error;
    }
  },

  // Register new user
  register: async (name, email, mobile, password) => {
    try {
      // Create URL-encoded form data
      const formData = new URLSearchParams();
      formData.append('json', JSON.stringify({
        api_key: API_KEY,
        add_user: true,
        name: name,
        email: email,
        mobile: mobile,
        password: password
      }));

      const response = await fetch(`${API_URL}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: formData.toString()
      });

      // Check if response is OK
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      // Try to parse the response as JSON
      const contentType = response.headers.get('content-type');
      if (contentType && contentType.includes('application/json')) {
        const data = await response.json();
        return data;
      } else {
        // If not JSON, get the text and log it
        const text = await response.text();
        console.error('Non-JSON response:', text);
        throw new Error('Server returned non-JSON response');
      }
    } catch (error) {
      console.error('Registration error:', error);
      throw error;
    }
  },

  // Request password reset
  forgotPassword: async (email) => {
    try {
      const response = await api.post('', formatData({ 
        email,
        send_mail_otp: true 
      }));
      return response.data;
    } catch (error) {
      console.error('Forgot password error:', error);
      throw error;
    }
  },

  // Logout user
  logout: () => {
    localStorage.removeItem('user');
  }
};

// Password management services
export const passwordService = {
  // Add a new password
  addPassword: async (userId, password, platform, email) => {
    try {
      const userData = JSON.parse(localStorage.getItem('user'));
      if (!userData || !userData.userId) {
        throw new Error('User ID not found. Please login again.');
      }

      const response = await fetch(`${API_URL}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          api_key: API_KEY,
          add_password: true,
          user_id: userData.userId,
          password: password,
          platform: platform,
          email: email
        }),
      });

      const data = await response.json();
      return data;
    } catch (error) {
      console.error('Error adding password:', error);
      throw error;
    }
  },

  // Get all passwords for a user
  getPasswords: async (email) => {
    try {
      const response = await api.post('', formatData({ 
        email,
        get_pass: true 
      }));
      
      if (response.data.code === '130') {
        return response.data;
      } else if (response.data.code === '132' || response.data.code === '133') {
        // Session expired or unauthorized
        authService.logout();
        throw new Error('Session expired. Please login again.');
      }
      
      return response.data;
    } catch (error) {
      console.error('Get passwords error:', error);
      throw error;
    }
  },

  // Update password
  updatePassword: async (passwordData) => {
    try {
      const response = await api.post('', formatData({
        id: passwordData.id,
        name: passwordData.platform,
        password: passwordData.password,
        update_password: true
      }));
      
      if (response.data.code === '150') {
        return response.data;
      } else if (response.data.code === '132' || response.data.code === '133') {
        // Session expired or unauthorized
        authService.logout();
        throw new Error('Session expired. Please login again.');
      }
      
      throw new Error(response.data.message || 'Failed to update password');
    } catch (error) {
      console.error('Update password error:', error);
      throw error;
    }
  },

  // Export passwords
  exportPasswords: async (email) => {
    try {
      const form = document.createElement('form');
      form.method = 'POST';
      form.action = API_URL;
      form.target = '_blank';
      
      const jsonInput = document.createElement('input');
      jsonInput.type = 'hidden';
      jsonInput.name = 'json';
      jsonInput.value = JSON.stringify({
        api_key: API_KEY,
        export_passwords: true,
        email: email,
        format: 'excel'
      });
      form.appendChild(jsonInput);
      
      document.body.appendChild(form);
      form.submit();
      document.body.removeChild(form);
      
      return { success: true };
    } catch (error) {
      console.error('Export passwords error:', error);
      throw error;
    }
  },

  // Import passwords
  importPasswords: async (email, csvFile) => {
    try {
      const reader = new FileReader();
      
      return new Promise((resolve, reject) => {
        reader.onload = async (event) => {
          try {
            const csvData = event.target.result;
            
            const response = await api.post('', formatData({
              import_passwords: true,
              email: email,
              csv_data: csvData
            }));
            
            if (response.data.code === '172') {
              resolve(response.data);
            } else if (response.data.code === '132' || response.data.code === '133') {
              // Session expired or unauthorized
              authService.logout();
              reject(new Error('Session expired. Please login again.'));
            } else {
              reject(new Error(response.data.message || 'Failed to import passwords'));
            }
          } catch (error) {
            reject(error);
          }
        };
        
        reader.onerror = () => {
          reject(new Error('Failed to read CSV file'));
        };
        
        reader.readAsText(csvFile);
      });
    } catch (error) {
      console.error('Import passwords error:', error);
      throw error;
    }
  },

  // Delete password
  deletePassword: async (passwordId) => {
    try {
      const response = await api.post('', formatData({
        delete_password: true,
        id: passwordId
      }));
      
      if (response.data.code === '160') {
        return response.data;
      } else if (response.data.code === '132' || response.data.code === '133') {
        // Session expired or unauthorized
        authService.logout();
        throw new Error('Session expired. Please login again.');
      }
      
      throw new Error(response.data.message || 'Failed to delete password');
    } catch (error) {
      console.error('Delete password error:', error);
      throw error;
    }
  },

  // Generate password
  generatePassword: async () => {
    try {
      const response = await api.post('', formatData({
        generate_password: true
      }));
      
      if (response.data.code === '180') {
        return response.data;
      }
      
      throw new Error('Failed to generate password');
    } catch (error) {
      console.error('Generate password error:', error);
      throw error;
    }
  }
};

export default api;
