import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import { Toaster } from 'react-hot-toast';
import Login from './components/Login';
import Register from './components/Register';
import Dashboard from './components/Dashboard';
import AddPassword from './components/AddPassword';
import ForgotPassword from './components/ForgotPassword';

// Dashboard wrapper component to handle navigation
const DashboardWrapper = ({ user, onLogout, onAddClick, isAuthenticated }) => {
  const navigate = useNavigate();
  
  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/', { replace: true });
    }
  }, [isAuthenticated, navigate]);
  
  if (!isAuthenticated) {
    return null;
  }
  
  return (
    <>
      <Dashboard
        user={user}
        onLogout={onLogout}
        onAddClick={onAddClick}
      />
      <AddPassword
        isOpen={false}
        onClose={() => {}}
        onAdd={() => {}}
        user={user}
      />
    </>
  );
};

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showAddPassword, setShowAddPassword] = useState(false);
  const [user, setUser] = useState(null);
  const [showForgotPassword, setShowForgotPassword] = useState(false);
  const [showRegister, setShowRegister] = useState(false);

  const handleLogin = (userData) => {
    setUser(userData);
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setUser(null);
  };

  const handleAddPassword = (newPassword) => {
    // This is now handled by the Dashboard component directly
    // We just need to close the modal
    setShowAddPassword(false);
  };

  const handleForgotPassword = () => {
    setShowForgotPassword(true);
  };

  const handleBackToLogin = () => {
    setShowForgotPassword(false);
    setShowRegister(false);
  };

  const handleShowRegister = () => {
    setShowRegister(true);
  };

  return (
    <Router future={{ v7_startTransition: true, v7_relativeSplatPath: true }}>
      <Toaster position="top-right" />
      <Routes>
        <Route
          path="/"
          element={
            isAuthenticated ? (
              <Navigate to="/dashboard" replace />
            ) : showForgotPassword ? (
              <ForgotPassword onBack={handleBackToLogin} />
            ) : showRegister ? (
              <Register onBack={handleBackToLogin} />
            ) : (
              <Login 
                onLogin={handleLogin} 
                onForgotPassword={handleForgotPassword}
                onShowRegister={handleShowRegister}
              />
            )
          }
        />
        <Route
          path="/dashboard"
          element={
            <DashboardWrapper
              user={user}
              onLogout={handleLogout}
              onAddClick={() => setShowAddPassword(true)}
              isAuthenticated={isAuthenticated}
            />
          }
        />
      </Routes>
      {isAuthenticated && (
        <AddPassword
          isOpen={showAddPassword}
          onClose={() => setShowAddPassword(false)}
          onAdd={handleAddPassword}
          user={user}
        />
      )}
    </Router>
  );
};

export default App;
